import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import AcUnitIcon from "@mui/icons-material/AcUnit";
import WbSunnyIcon from "@mui/icons-material/WbSunny";
import ThunderstormIcon from "@mui/icons-material/Thunderstorm";
import "./InfoBox.css";

export default function InfoBox({ info }) {
  let img =
    "data:image/jpeg;base64,";
  let hotImg =
    "data:image/jpeg;base64,";
  let coldImg =
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRlN5Yqe6oYKE6c3a-c1vSOY36-WXBTuC-A9g&s";
  let rainImg =
    "data:image/jpeg;base64,";
  return (
    <div className="Info">
      <div className="InfoContainer">
        <Card sx={{ maxWidth: 345 }}>
          <CardMedia
            sx={{ height: 140 }}
            image={
              info.humidity > 80 ? rainImg : info.temp > 15 ? hotImg : coldImg
            }
            title="clear Sky"
          />
          <CardContent>
            <Typography gutterBottom variant="h5" component="div">
              {info.city}&nbsp;
              {info.humidity > 80 ? (
                <ThunderstormIcon />
              ) : info.temp > 15 ? (
                <WbSunnyIcon />
              ) : (
                <AcUnitIcon />
              )}
            </Typography>
            <Typography
              variant="body2"
              sx={{ color: "text.secondary" }}
              component={"span"}
            >
              <p>Temp : {info.temp}&deg;C</p>
              <p>Min-Temp : {info.tempMin}&deg;C</p>
              <p>Max-Temp : {info.tempMax}&deg;C</p>
              <p>Humidity : {info.humidity}&deg;C</p>
              <p>
                The weather can be described as <b>{info.weather}</b> and
                feelslike {info.feelLike}&deg;C
              </p>
            </Typography>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}
